import React from "react";
import axios from "axios";
import Swal from "sweetalert2";
import api from "app/hooks/api";
import history from "@history";
import NotificationManager from "react-notifications/lib/NotificationManager";
import { login } from "app/services/AuthService";
import { nanoid } from "nanoid";
export const ADD_PRODUCT = "ADD_PRODUCT";
export const GET_PRODUCTS = "GET_PRODUCTS";
export const GET_BIDDINGS = "GET_BIDDINGS";
export const ADD_BIDDING = "ADD_BIDDING";

export const getProducts = () => (dispatch) => {
  const response = api.get("/products");
  const data = response.data.data;
  dispatch({
    type: GET_PRODUCTS,
    payload: data,
  });
};

export const addProduct = (product, user) => (dispatch) => {
  try {
    const newProduct = {
      ...product,
      user: user.uid,
      isDone: false,
    };
    const response = api.post("/products", newProduct);
    const data = response.data.data;
    dispatch({
      type: ADD_PRODUCT,
      payload: data,
    });
    NotificationManager.success("Product Added Successfully");
    history.push("/seller/my-products");
  } catch (error) {
    console.log(error);
  }
};
