import React, { useState } from "react";
import { Breadcrumb } from "@gull";
import { format } from "date-fns";
import ReactPaginate from "react-paginate";
import { MdDelete } from "react-icons/md";
import { Link } from "react-router-dom";
import { useAuth } from "app/appContext";
const MyProducts = () => {
  const { products, user } = useAuth();
  const [rowsPerPage, setRowPerPage] = useState(10);
  const [page, setPage] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const userList = [];
  const handleDelete = (id) => {};
  const updatePageData = () => {
    // getAllUser().then(({ data }) => this.setState({ userList: [...data] }));
  };

  const filteredProducts = products.filter(
    (product) => product.userId === user.uid
  );
  const handlePageClick = (data) => {
    let page = data.selected;
    setPage(page);
  };

  const handleDeleteContact = (values) => {};

  return (
    <div>
      <Breadcrumb routeSegments={[{ name: "Product List" }]}></Breadcrumb>
      <section className="contact-list">
        <div className="row">
          <div className="col-md-12 mb-4">
            <div className="card text-left">
              <div className="card-header text-right bg-transparent"></div>

              <div className="row px-4 mt-3">
                <div className="col-sm-12 col-md-6 mb-2">
                  <div className="d-flex align-items-center">
                    <span className="mr-1">Show</span>
                    <div className="mr-1">
                      <select
                        className="form-control"
                        onChange={({ target: { value } }) => {
                          setRowPerPage(value);
                        }}
                      >
                        <option value={10}>10</option>
                        <option value={25}>25</option>
                        <option value={50}>50</option>
                        <option value={100}>100</option>
                      </select>
                    </div>
                    <span>entries</span>
                  </div>
                </div>
                <div className="col-sm-12 col-md-6 mb-2">
                  <div className="d-flex justify-content-lg-end align-items-center">
                    <span className="mr-1">Search:</span>
                    <div className="mr-1">
                      <input
                        type="search"
                        className="form-control form-control-sm"
                        placeholder=""
                        // onChange={this.handleSearch}
                        aria-controls="ul-contact-list"
                      />
                    </div>
                  </div>
                </div>
              </div>

              <div className="card-body pt-1">
                <div className="table-responsive">
                  <table id="ul-contact-list" className="display table w-100">
                    <thead>
                      <tr>
                        <th>Product Name</th>
                        <th>Product Images</th>
                        <th>Category</th>
                        <th>Bid End Date</th>
                        <th>Bid Status</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {filteredProducts &&
                        filteredProducts
                          .filter((product) => product.productName)
                          .slice(rowsPerPage * page, rowsPerPage * (page + 1))
                          .map((product, ind) => (
                            <tr key={ind}>
                              <td valign="middle">{product.productName}</td>
                              <td>
                                {product.productImages.map((file) => (
                                  <img
                                    src={file}
                                    alt="Preview"
                                    key={file}
                                    style={{
                                      width: "50px",
                                      height: "50px",
                                      marginLeft: "5px",
                                    }}
                                  />
                                ))}
                              </td>
                              <td>{product.category}</td>
                              <td>
                                {format(
                                  new Date(product.endDate),
                                  "dd/MM/yyy HH:mm"
                                )}
                              </td>
                              <td>
                                {new Date(product.endDate) <= Date.now() ? (
                                  <Link
                                    to={`/seller/add-invoice/${product.id}`}
                                    className="btn btn-info"
                                  >
                                    Create Invoice
                                  </Link>
                                ) : (
                                  "Ongoing"
                                )}
                              </td>
                              <td>
                                <div className="cursor-pointer">
                                  <MdDelete
                                    className="text-danger"
                                    size={18}
                                  ></MdDelete>
                                </div>
                              </td>
                            </tr>
                          ))}
                    </tbody>
                  </table>
                </div>
                <div className="d-flex justify-content-end mr-lg-4">
                  <ReactPaginate
                    previousLabel={"Previous"}
                    nextLabel={"Next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={Math.ceil(userList.length / rowsPerPage)}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={3}
                    onPageChange={handlePageClick}
                    containerClassName={"pagination pagination-lg"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default MyProducts;
