import React, { Component, Fragment } from "react";
import {
  Button,
  Form,
  FormGroup,
  FormControl,
  FormLabel,
} from "react-bootstrap";
import { Formik, FieldArray } from "formik";
import DatePicker from "react-datepicker";
import * as yup from "yup";
import { Breadcrumb } from "@gull";
import { useAuth } from "app/appContext";
import { useParams } from "react-router-dom";
const invoiceSchema = yup.object().shape({
  orderDate: yup.date().required("Order Date is required"),
  vat: yup.number().required("unit is required"),
  grandTotal: yup.number().required("unit is required"),
});
const CreateInvoice = () => {
  const { products, users, biddings } = useAuth();
  const { id } = useParams();
  const product = products.find((product) => product.id === id);
  const filteredBiddings = biddings.filter(
    (bidding) => bidding.productId === id
  );
  const findWinner =
    filteredBiddings.length !== 0 &&
    filteredBiddings.reduce((acc, current) => {
      if (current.bidAmount > acc.bidAmount) {
        return current;
      } else {
        return acc;
      }
    });
  const handleSubmit = () => {};
  const initialState = {};
  return (
    <div>
      <Breadcrumb routeSegments={[{ name: "Product List" }]}></Breadcrumb>
      <div className="invoice-viewer py-3">
        <Formik
          initialValues={initialState}
          validationSchema={invoiceSchema}
          onSubmit={handleSubmit}
          enableReinitialize={true}
        >
          {({
            values,
            errors,
            touched,
            handleChange,
            handleBlur,
            handleSubmit,
            isSubmitting,
            setSubmitting,
            setFieldValue,
          }) => {
            return (
              <Fragment>
                <Form onSubmit={handleSubmit} className="px-3">
                  <div className="viewer_actions d-flex justify-content-end">
                    <div className="mb-4">
                      <Button
                        type="button"
                        className="mr-3 py-2"
                        variant="warning"
                        onClick={() => this.props.toggleInvoiceEditor()}
                      >
                        Cancel
                      </Button>
                      <Button
                        type="submit"
                        className="py-2"
                        variant="primary"
                        // disabled={loading}
                      >
                        Save
                      </Button>
                    </div>
                  </div>

                  <div className="row justify-content-between">
                    <div className="col-md-6">
                      <div className="form-group mb-3">
                        <div className="mb-1">Order Date</div>
                        <DatePicker
                          className="form-control text-right"
                          dateFormat="dd/MM/yyyy"
                          selected={new Date(values.date)}
                          onChange={(date) => {
                            setFieldValue("date", date);
                          }}
                        />
                      </div>
                    </div>
                  </div>
                  <div className="mt-3 mb-4 border-top"></div>
                  <div className="row mb-5">
                    <div className="col-md-6">
                      <h5 className="font-weight-bold">Bill From</h5>
                      <h5></h5>
                    </div>

                    <div className="col-md-6 text-right">
                      <h5 className="font-weight-bold">Bill To</h5>
                      <h5></h5>
                    </div>
                  </div>
                  <div className="row">
                    <div className="col-md-12 table-responsive">
                      <table className="table table-hover mb-3">
                        <thead className="bg-gray-300">
                          <tr>
                            <th scope="col">Product Name</th>
                            <th scope="col">Product Image</th>
                            <th scope="col">Unit Price</th>
                            <th scope="col"></th>
                          </tr>
                        </thead>
                        <tbody>
                          <tr key={ind}>
                            <th className="text-middle" scope="row">
                              {ind + 1}
                            </th>
                            <td></td>

                            <td className="text-middle"></td>
                            <td></td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                    <div className="col-md-12">
                      <div className="invoice-summary invoice-summary-input float-right">
                        <p className="d-flex align-items-center">
                          Vat(%):
                          <span>
                            <FormControl
                              type="text"
                              className="small-input"
                              name="vat"
                              value={values.vat}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              isInvalid={errors.vat && touched.vat}
                            />
                          </span>
                        </p>
                        <h5 className="font-weight-bold d-flex align-items-center">
                          Grand Total:
                          <span>
                            <FormControl
                              type="text"
                              className="small-input"
                              name="grandTotal"
                              value={values.grandTotal}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              isInvalid={
                                errors.grandTotal && touched.grandTotal
                              }
                            />
                          </span>
                        </h5>
                      </div>
                    </div>
                  </div>
                </Form>
              </Fragment>
            );
          }}
        </Formik>
      </div>
    </div>
  );
};

export default CreateInvoice;
