import React, { useState } from "react";
import { Breadcrumb } from "@gull";
import { format } from "date-fns";
import ReactPaginate from "react-paginate";
import { MdDelete } from "react-icons/md";
import { Link } from "react-router-dom";
import { useAuth } from "app/appContext";
import { deleteProduct } from "app/reducers/actions/FurnitureActions";
const MyProducts = () => {
  const { products, user, dispatch } = useAuth();
  const [rowsPerPage, setRowPerPage] = useState(10);
  const [page, setPage] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const userList = [];
  const handleDelete = (id) => {};
  const updatePageData = () => {
    // getAllUser().then(({ data }) => this.setState({ userList: [...data] }));
  };

  const filteredProducts = products.filter(
    (product) => product.userId === user.uid
  );
  const handlePageClick = (data) => {
    let page = data.selected;
    setPage(page);
  };

  const handleDeleteProduct = async (id) => {
    await deleteProduct(id)(dispatch);
    console.log(id);
  };

  return (
    <div>
      <Breadcrumb routeSegments={[{ name: "Product List" }]}></Breadcrumb>

      <div className="card-body pt-1">
        <div className="table-responsive">
          <table>
            <thead>
              <tr>
                <th className="border-0 font-weight-semibold text-info">
                  Product Name
                </th>
                <th>Product Images</th>
                <th>Category</th>
                <th>Bid End Date</th>
                <th>Bid Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredProducts &&
                filteredProducts
                  .filter((product) => product.productName)
                  .slice(rowsPerPage * page, rowsPerPage * (page + 1))
                  .map((product, ind) => (
                    <tr key={ind}>
                      <td valign="middle">{product.productName}</td>
                      <td>
                        {product.productImages.map((file) => (
                          <img
                            src={file}
                            alt="Preview"
                            key={file}
                            style={{
                              width: "50px",
                              height: "50px",
                              marginLeft: "5px",
                            }}
                          />
                        ))}
                      </td>
                      <td>{product.category}</td>
                      <td>
                        {format(new Date(product.endDate), "dd/MM/yyy HH:mm")}
                      </td>
                      <td>
                        {new Date(product.endDate) <= Date.now() ? (
                          <Link
                            to={`/seller/add-invoice/${product.id}`}
                            className="btn btn-info"
                          >
                            Create Invoice
                          </Link>
                        ) : (
                          "Ongoing"
                        )}
                      </td>
                      <td>
                        <div className="cursor-pointer">
                          <MdDelete
                            onClick={() => handleDeleteProduct(product.id)}
                            className="text-danger"
                            size={18}
                          ></MdDelete>
                        </div>
                      </td>
                    </tr>
                  ))}
            </tbody>
          </table>
        </div>
        <div className="d-flex justify-content-end mr-lg-4">
          <ReactPaginate
            previousLabel={"Previous"}
            nextLabel={"Next"}
            breakLabel={"..."}
            breakClassName={"break-me"}
            pageCount={Math.ceil(userList.length / rowsPerPage)}
            marginPagesDisplayed={2}
            pageRangeDisplayed={3}
            onPageChange={handlePageClick}
            containerClassName={"pagination pagination-lg"}
            subContainerClassName={"pages pagination"}
            activeClassName={"active"}
          />
        </div>
      </div>
    </div>
  );
};

export default MyProducts;
